mapper.select:
SELECT si.id, si.img_name, si.img_path, si.user_id, u.nick_name as user_name, si.upload_time, si.visible, si.type, di.dict_item_name as type_name FROM sys_images si LEFT JOIN user u ON si.user_id = u.id LEFT JOIN dict di ON si.type = di.dict_type_id AND di.dict_type_name = '图片类别'
AND si.type = #{type}
AND u.nick_name LIKE CONCAT('%', #{userName}, '%')

mapper.update:
UPDATE sys_images
SET visible = #{visible}
WHERE id = #{id}

UPDATE sys_images
SET img_name = #{imgName},type = #{type}
WHERE id = #{id}
