mapper.select:
SELECT u.*, d.dict_item_name as dict_item_name, d.dict_color as dict_color FROM user u LEFT JOIN dict d ON u.user_role = d.dict_type_id
AND u.user_name LIKE CONCAT('%', #{user.userName}, '%')
AND u.user_sex LIKE CONCAT('%', #{user.userSex}, '%')
AND u.user_role = #{user.userRole}
AND u.user_status = #{user.userStatus}
AND u.user_date BETWEEN #{startTime} AND #{endTime}
AND u.user_role IN (0, 2) AND d.dict_type_name = 'role'
ORDER BY u.id DESC

mapper.update:
UPDATE user
SET user_version = #{version}
WHERE id = #{id} AND user_name = #{userName}

UPDATE user
SET user_version = #{version}
WHERE id = #{id}

UPDATE user
SET user_status = #{user_status}
WHERE id = #{user_id}

mapper.select:
SELECT user_name,nick_name
FROM user
WHERE id = #{Id}

mapper.update:
UPDATE user
SET user_role = #{roleNumber}
WHERE id = #{userId}
